class point extends Basepoint {
	
	public static $outputSeparator = ',';
	
	public function getOutput() {
		if($this->isNew()) {
			return null;
		}
		
		$coord = $this->getLongitude()
			.self::$outputSeparator
			.$this->getLatitude();

		$altitude = (float)$this->getAltitude();
		if(!empty($altitude)) {
			$coord .= self::$outputSeparator.$altitude;
		}

		return $coord;
	}
}
